<?phpclass Hosting_Form_Plan extends Zend_Form {    public function init() {        $this->setName('plan');        $name = $this->CreateElement('text', 'name')                        ->setLabel('Plan Name')                        ->setRequired(TRUE)                        ->setAttrib('class', 'ltr')                        ->addValidator('NotEmpty')                        ->addFilter('StringTrim')                        ->addFilter('StripTags')                        ->addValidator('StringLength', false, array(10, 20))                        ->addValidator('Regex', false, array('/[a-zA-Z0-9_.-]$/'))                        /* ->addValidator('Db_NoRecordExists', false, array(                          'table' => 'hosting_plan'                          , 'field' => 'name')) */                        ->setDescription('Accept Dot, Dash, Underline, Number and Alphabetic');        $qouta = $this->CreateElement('text', 'qouta')                        ->setLabel('Disk Space Qouta')                        ->setRequired(TRUE)                        ->setAttrib('class', 'ltr')                        ->addValidator('Digits')                        ->addValidator('Between', false, array('min' => 0, 'max' => 40000))                        ->setDescription('Unit: MB');        $bandwidth = $this->CreateElement('text', 'bandwidth')                        ->setLabel('Bandwidth')                        ->setAttrib('class', 'ltr')                        ->setRequired(TRUE)                        ->addValidator('Digits')                        ->addValidator('Between', false, array('min' => 0, 'max' => 400000))                        ->setDescription('Unit: MB');        $reseller = $this->CreateElement('radio', 'reseller')                        ->setLabel('Reseller')                        ->addMultiOptions(array(                            '0' => 'No',                            '1' => 'Yes'))                        ->setRequired(true)                        ->addValidator('NotEmpty');        $cost_per_month = $this->CreateElement('text', 'cost_per_month')                        ->setLabel('Cost per month')                        ->setRequired(true)                        ->setAttrib('class', 'ltr')                        ->addValidator('NotEmpty')                        ->addValidator('Digits')                        ->setDescription('Unit: Rial');        $submit = $this->CreateElement('submit', 'submit')                        ->setLabel('Submit');        $this->addElements(array($name, $qouta, $bandwidth, $reseller, $cost_per_month, $submit));    }}