<?phpclass Monasebat_Form_Ads extends Zend_Form {    public function init() {	$this->setName('Ads');        $this->setAttrib('enctype', 'multipart/form-data');	$title = $this->CreateElement('text', 'title')			->setLabel('Title')			->setRequired(true)			->addValidator('NotEmpty')			->addValidator('Alnum',false,array('allowWhiteSpace'=>true))			->setDescription('Advertisment Title');        $link = $this->CreateElement('text', 'link')			->setLabel('link')			->setRequired(false)			->addValidator('Hostname', false)			->setDescription('example: http://www.roshd.ir');        $describe = $this->CreateElement('text', 'describe')			->setLabel('Describe')			->setRequired(true)			->addValidator('NotEmpty')			->addValidator('Alnum',false,array('allowWhiteSpace'=>true))			->setDescription('Advertisment Describe');        $image = $this->CreateElement('file', 'image')			->setLabel('Image')			->setRequired(true)			->addValidator('NotEmpty')			->addValidator('Size', false, '2MB')                        ->addValidator('Count', false, 1)                        ->addValidator('Extension', false, 'jpg,gif,png')                        ->addValidator('ImageSize', false, array('minwidth'=>455,'maxwidth'=>455,'minheight'=>88,'maxheight'=>88))			->setDescription('Allowed dimension is 455 Ã— 88');        $submit = $this->CreateElement('submit', 'submit')			->setLabel('Submit');        $this->addElements(array($title, $link, $describe, $image, $submit));    }}