<?phpclass Monasebat_Form_Menu extends Zend_Form {    public function init() {	$this->setName('Menu');        $this->setAttrib('enctype', 'multipart/form-data');	$title = $this->CreateElement('text', 'title')			->setLabel('Title')			->setRequired(true)			->addValidator('NotEmpty')			->addValidator('Alnum',false,array('allowWhiteSpace'=>true))			->setDescription('Menu Title');        $link = $this->CreateElement('text', 'link')			->setLabel('link')			->setRequired(false)			->addValidator('Hostname', false)			->setDescription('example: http://www.roshd.ir');        $place = $this->CreateElement('select', 'place')			->setLabel('Place')			->setMultiOptions(array(				'top' => 'Top',				'bottom' => 'Bottom'			))			->setRequired(true)			->addValidator('NotEmpty', true)			->addValidator('Alpha',false,array('allowWhiteSpace'=>true))			->setDescription('Place of menu item');        $submit = $this->CreateElement('submit', 'submit')			->setLabel('Submit');        $this->addElements(array($title, $link, $place, $submit));    }}